//nolint:scopelint
func TestParseRules(t *testing.T) {
	tests := []struct {
		name    string
		lines   string
		want    int
		wantErr bool
	}{
		{
			name:    "example",
			lines:   set0,
			want:    4,
			wantErr: false,
		},
		{
			name:    "actual puzzle",
			lines:   set1,
			want:    238,
			wantErr: false,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			rules, err := day7.ParseRules(tt.lines)
			if (err != nil) != tt.wantErr {
				t.Errorf("ParseRules() error = %v, wantErr %v", err, tt.wantErr)

				return
			}

			got := rules.CountBagsWhichMayContainAtLeast("shiny gold")

			if !reflect.DeepEqual(got, tt.want) {
				t.Errorf("ParseRules() got = %v, want %v", got, tt.want)
			}

			fmt.Printf("AdventOfCode/2020/Day/7/1: count at least bags: %d\n", got)
		})
	}
}

//nolint:scopelint
func TestCountTotalBagsFor(t *testing.T) {
	tests := []struct {
		name    string
		lines   string
		want    int
		wantErr bool
	}{
		{
			name:    "example",
			lines:   set2,
			want:    126,
			wantErr: false,
		},

		{
			name:    "actual puzzle",
			lines:   set1,
			want:    82930,
			wantErr: false,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			rules, err := day7.ParseRules(tt.lines)
			if (err != nil) != tt.wantErr {
				t.Errorf("ParseRules() error = %v, wantErr %v", err, tt.wantErr)

				return
			}

			got := rules.CountTotalBagsFor("shiny gold")

			if !reflect.DeepEqual(got, tt.want) {
				t.Errorf("ParseRules() got = %v, want %v", got, tt.want)
			}

			fmt.Printf("AdventOfCode/2020/Day/7/2: total bags: %d\n", got)
		})
	}
}
